clc;clear;%% this is just a code to test conversion of symmetric matrix to tridiagonal matrix using a similarity transformation.% creating psd matricea1 = [1,2,3,4,7;4,5,6,2,3;7,18,9,8,9;8,2,5,2,5;7,2,4,8,2];	a = a1*a1';% hessen function is created to convert given matrice into hessenberg form% go that function hessen for procedure of how it doneh = hessen(a);disp('given matrice');disp(a);disp('its hessenberg tridiagonal form');disp(h);